def count_xmas_patterns(grid):
    rows = len(grid)
    cols = len(grid[0])
    count = 0

    # Helper function to check if a given position forms an X-MAS pattern
    def is_xmas_pattern(r, c):
        # Check boundaries for X-MAS pattern
        if r - 1 < 0 or r + 1 >= rows or c - 1 < 0 or c + 1 >= cols:
            return False
        
        # Top-left, top-right, center, bottom-left, bottom-right
        top_left = grid[r - 1][c - 1]
        top_right = grid[r - 1][c + 1]
        center = grid[r][c]
        bottom_left = grid[r + 1][c - 1]
        bottom_right = grid[r + 1][c + 1]

        # Check for valid X-MAS patterns
        patterns = [
            ("M", "S", "A", "M", "S"),  # MAS top-left to bottom-right
            ("S", "M", "A", "S", "M"),  # SAM bottom-left to top-right
            ("M", "A", "S", "M", "A"),  # MAS flipped
            ("S", "A", "M", "S", "A")   # SAM flipped
        ]
        return (top_left, top_right, center, bottom_left, bottom_right) in patterns

    # Iterate over each cell in the grid
    for r in range(rows):
        for c in range(cols):
            if is_xmas_pattern(r, c):
                count += 1
    
    return count

